package com.simplilearn.datatypes;

public class BooleanExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean b1=true;
		System.out.println("value of boolean b1 : "+b1); // value of boolean b1 : true
		
		boolean b2=false;
		System.out.println("value of boolean b2 :"+b2); //value of boolean b2 : false
		
		// always respond in yes or no
	}

}
