package com.simplilearn.datatypes;

public class IntExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i1=12345678;
		System.out.println("value of i1 : "+i1);  //value of i1 : 12345678
		
		int i2=247483647;
		System.out.println("value of i2 : "+i2);  //value of i2 : 247483647
		
		// int i3=2147483648;
		// System.out.println("value of i3 : "+i3);  
		// (error)
		// range is between -2147483648 to 2147483647
	}

}
