package com.simplilearn.dowhileloop;

public class DoWhileExample3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		do {
			System.out.println("a is less than b");
		} while (a<b);
		// a is less than b is printed infinity times
		// to stop the infinity process here we have to add 'a++'

		
	}

}
