package com.simplilearn.whileloop;

public class WhileLoopExample2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 //case-2
		int x=10;
		int y=20;
		 while(x<y) {
			 System.out.println("x is less than y");
			 x++;
			  }
		 System.out.println(" i am done");
// its not done until its satisfied
		 
		 //here the answer wiLl printed is x is less than y (continuously- for specific times )
		 //and at last i am done is printed
		 
	}

}
